import React, { useState } from "react";
import reviews from "./data";
import Title from "./components/Title";
import Idea from "./components/Idea";
import { FiChevronLeft, FiChevronRight } from "react-icons/fi";
import { FaQuoteRight, FaQuoteLeft } from "react-icons/fa";


const App = () => {

  const[index,setIndex] =useState(0);
  function leftShiftHandler(){
    index-1 <0 ? setIndex(reviews.length-1):setIndex(index-1);

  };
  function rightShiftHandler(){
    index+1 ===reviews.length ?setIndex(0) : setIndex(index+1);

  };
  function surpriceShiftHandler(){
    const supriceIndex= Math.floor(Math.random()*reviews.length);
    setIndex(supriceIndex);

  };
  return (
    <div>

      <div className="h-[100vh] w-[100vw] bg-red-800 ">
        <div className="bg-blue-600 flex justify-center  align w-[20%]">for worlds 1% readers!</div>

      <div className=" ">48 LAWS OF POWER</div>

       <p>~robert greene</p>

        <div> <FaQuoteLeft /></div>

        <Title review={reviews[index]}/>

        <div> <FaQuoteRight /></div>

        <Idea review={reviews[index]}/>

        <div>

          <button onClick={leftShiftHandler}> <FiChevronLeft /></button>

          <button onClick={rightShiftHandler}><FiChevronRight /></button>

        </div>

        <button onClick={surpriceShiftHandler}>learn great!</button>
      </div>

    </div>
  )
};

export default App;
