import React from 'react'



function Idea(props) {
    const reviews = props.review;
    return ( 
        // <div className=" h-[40%] w-9/12 mx-auto bg-gray-900 rounded-lg">

        //     <p className=" font-mono text-l text-white p-3 ">{reviews.idea}</p>

        // </div>
         <div>
         {reviews.map((review) => (
           <div key={review.id}>
             <h2>{review.title}</h2>
             <ul>
               {review.idea.split('\n').map((point, index) => (
                 <li key={index}>{point.trim()}</li>
               ))}
             </ul>
           </div>
         ))}
       </div>
     );
}

export default Idea;
