import React, { useState } from "react";
import reviews from "./data";
import Title from "./components/Title";
import Idea from "./components/Idea";
import { FiChevronLeft, FiChevronRight } from "react-icons/fi";
import { FaQuoteRight, FaQuoteLeft } from "react-icons/fa";


const App = () => {

  const[index,setIndex] =useState(0);
  function leftShiftHandler(){
    index-1 <0 ? setIndex(reviews.length-1):setIndex(index-1);

  };
  function rightShiftHandler(){
    index+1 ===reviews.length ?setIndex(0) : setIndex(index+1);

  };
  function surpriceShiftHandler(){
    const supriceIndex= Math.floor(Math.random()*reviews.length);
    setIndex(supriceIndex);

  };
  return (
    <div className="h-full w-full bg-gradient-to-r from-purple-500 to-blue-600">
    <div className="flex flex-col items-center justify-center min-h-screen p-4">
      <div className="text-3xl sm:text-5xl md:text-6xl font-extrabold text-white text-center">
        48 LAWS OF POWER
      </div>
      <p className="mt-3 text-sm sm:text-base md:text-lg font-medium text-white text-center">
        ~ Robert Greene
      </p>

      <div className="text-white text-3xl mt-8">
        <FaQuoteLeft />
      </div>

      <Title review={reviews[index]} />

      <div className="text-white text-3xl mb-8">
        <FaQuoteRight />
      </div>

      <Idea review={reviews[index]} />

      <div className="flex gap-5 m-4 mt-9">
        <button onClick={leftShiftHandler} className="text-4xl sm:text-5xl text-white">
          <FiChevronLeft />
        </button>
        <button onClick={rightShiftHandler} className="text-4xl sm:text-5xl text-white">
          <FiChevronRight />
        </button>
      </div>

      <button
        onClick={surpriceShiftHandler}
        className="mx-auto text-white bg-gray-900 rounded-md w-32 sm:w-40 h-10 text-lg sm:text-xl mt-5"
      >
        New One!
      </button>
    </div>
  </div>
  )
};

export default App;
